MaximumAttendees=100
HardLimit=120
EntryTemplate=#if($item.Data.Name)$item.Data.Name#else$item.UserName#end#if($item.Data.Email), <a href="mailto:$item.Data.Email">E-Mail</a>#end#if($item.Data.Blog), <a href="$item.Data.Blog">Blog</a>#end#if($item.Data.Twitter), <a href="http://twitter.com/$item.Data.Twitter/">Twitter</a>#end#if($item.Data.Xing), <a href="http://xing.com/profile/$item.Data.Xing">XING</a>#end#if($item.Data.Picture), <a href="$item.Data.Picture">Bild</a>#end#if($item.Data.Sponsoring > 0), $item.Data.FormattedSponsoring &euro;#end